def is_prime(num):
    if num <= 1:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False

    for i in range(3, int(num**0.5) + 1, 2):
        if num % i == 0:
            return False

    return True

def count_primes_up_to_n(n):
    count = 0
    for i in range(1, n + 1):
        if is_prime(i):
            count += 1
    return count

