def count_components(graph):
    def dfs(node, visited):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs(neighbor, visited)

    n = len(graph)
    visited = [False] * n
    components = 0

    for i in range(n):
        if not visited[i]:
            dfs(i, visited)
            components += 1

    return components

