def find_closest_element(arr, k):
    low = 0
    high = len(arr) - 1

    while low <= high:
        mid = (low + high) // 2

        if arr[mid] == k:
            return arr[mid]
        elif arr[mid] < k:
            low = mid + 1
        else:
            high = mid - 1

    if low == 0:
        return arr[0]
    elif high == len(arr) - 1:
        return arr[-1]
    else:
        if abs(arr[low] - k) < abs(arr[high] - k):
            return arr[low]
        else:
            return arr[high]

# example:
