print(result)

این کد از الگوریتم جستجوی دودویی برای پیدا کردن نزدیک‌ترین عدد به عدد k در آرایه مرتب شده استفاده می‌کند. الگوریتم به صورت بازگشتی کمترین و بزرگ‌ترین اعدادی را که از عدد k کوچک‌تر و بزرگ‌تر هستند، برای تعیین نزدیک‌ترین عدد به k پیدا می‌کند.

import random

def partition(arr, low, high):
    pivot_index = random.randint(low, high) 
    arr[pivot_index], arr[high] = arr[high], arr[pivot_index] 
    pivot = arr[high]
    i = low
    for j in range(low, high):
        if arr[j] < pivot:
            arr[i], arr[j] = arr[j], arr[i]
            i += 1
    arr[i], arr[high] = arr[high], arr[i]
    return i

def quick_select(arr, low, high, k):
    if low == high:
        return arr[low]

    pivot_index = partition(arr, low, high)
    if k == pivot_index:
        return arr[k]
    elif k < pivot_index:
        return quick_select(arr, low, pivot_index - 1, k)
    else:
        return quick_select(arr, pivot_index + 1, high, k)

def find_median(arr):
    n = len(arr)
    if n % 2 == 1:
        return quick_select(arr, 0, n - 1, n // 2)
    else:
        left_median = quick_select(arr, 0, n - 1, (n - 1) // 2)
        right_median = quick_select(arr, 0, n - 1, n // 2)
        return (left_median + right_median) / 2

# example:
arr = [3, 1, 4, 2, 5]
median = find_median(arr)
print(median)

