def print_pascal_triangle(n):
    for i in range(1, n+1):
        for j in range(0, n-i+1):
            print(" ", end="")

        num = 1
        for j in range(1, i+1):
            print(num, " ", end="")
            num = num * (i - j) // j

        print()

