def	dfs(adj,	mark,	h,	v,	b,	way)	:
	if	v	==	b:
		way.insert(0,	v)
		return	h[v]
	mark[v]	=	1
	for	u	in	adj[v]	:
		if	mark[u]	==	0	:
			h[u]	=	h[v]	+	1	
			r	=	dfs(adj,	mark,	h,	u,	b,	way)
			if	r	!=	-1	:
				way.insert(0,	v)
				return	r
	return	-1

