void	shuffle_card(int*	card)
{
	int	i,	r,	temp;
	for	(temp	=	0,	i	=	0;	temp	<	44;	i++,	temp++)
		card[temp]	=	i;
	/*زمان سیستم را به عنوان دانه برای ساخت اعداد رندوم میگیرد.*/
	srand(time(NULL));
	for	(i	=	43;	i	>	0;	i--)
	{
		/*اعداد رندوم برمیگرداند.*/
		r	=	rand()	%	i;
		temp	=	card[i];
		card[i]	=	card[r];
		card[r]	=	temp;
	}
}

