string	to_upper(string	&line,	int	index)
{
	string	result;
	result.push_back(captalize(line[index]));
	if	(index	>=	(line.length() -	1))
	{
		return	result;
	}
	result.append(to_upper(line,	index	+	1));
	return	result;
}

